import React from 'react';

interface SidebarProps {
  project: any;
  title: string;
  description: string;
  status: 'In Progress' | 'Completed' | 'On Hold';
  setTitle: (title: string) => void;
  setDescription: (description: string) => void;
  setStatus: (status: 'In Progress' | 'Completed' | 'On Hold') => void;
  onClose: () => void;
  handleSave: () => void;
}

const Sidebar: React.FC<SidebarProps> = ({
  project,
  title,
  description,
  status,
  setTitle,
  setDescription,
  setStatus,
  onClose,
  handleSave,
}) => {
  if (!project) return null;

  return (
    <div
      className={`fixed right-0 top-0 h-full w-1/3 bg-white shadow-lg p-4 transform transition-transform duration-300 ${
        project ? 'translate-x-0' : 'translate-x-full'
      }`}
    >
      <h2 className="text-xl font-bold mb-4">Edit Project</h2>
      <input
        type="text"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        className="border p-2 mb-4 block w-full rounded"
        placeholder="Title"
      />
      <textarea
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        className="border p-2 mb-4 block w-full rounded"
        placeholder="Description"
      />
      <select
        value={status}
        onChange={(e) => setStatus(e.target.value as 'In Progress' | 'Completed' | 'On Hold')}
        className="border p-2 mb-4 block w-full rounded"
      >
        <option value="In Progress">In Progress</option>
        <option value="Completed">Completed</option>
        <option value="On Hold">On Hold</option>
      </select>
      <div className="flex justify-end">
        <button onClick={onClose} className="bg-gray-500 text-white py-2 px-4 rounded mr-2">
          Cancel
        </button>
        <button onClick={handleSave} className="bg-blue-500 text-white py-2 px-4 rounded">
          Save
        </button>
      </div>
    </div>
  );
};

export default Sidebar;
