import React, { useState, useEffect } from 'react';

interface Project {
  title: string;
  description: string;
  status: 'In Progress' | 'Completed' | 'On Hold';
}

interface SidebarProps {
  project: Project | null;
  onClose: () => void;
  onSave: (updatedProject: Project) => void;
}

const Sidebar = ({ project, onClose, onSave }: SidebarProps) => {
  // State for managing form fields and whether the sidebar is open
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [status, setStatus] = useState<'In Progress' | 'Completed' | 'On Hold'>('In Progress');
  const [isOpen, setIsOpen] = useState(false); // Controls sidebar visibility
  const [shouldRender, setShouldRender] = useState(false); // Controls if the sidebar should be rendered

  // When project changes, update the form and show/hide the sidebar
  useEffect(() => {
    if (project) {
      // If a project is passed in, fill the form and show the sidebar
      setTitle(project.title);
      setDescription(project.description);
      setStatus(project.status);
      setShouldRender(true); // Render sidebar
      setTimeout(() => setIsOpen(true), 10); // Small delay for animation
    } else {
      // If no project, close the sidebar and unmount after animation
      setIsOpen(false); // Start closing animation
      setTimeout(() => setShouldRender(false), 300); // Unmount after 300ms
    }
  }, [project]);

  // Function to handle saving the form
  const handleSave = () => {
    if (project) {
      // If there's a project, pass the updated data back to parent
      onSave({ title, description, status });
    }
    setIsOpen(false); // Start closing animation
    setTimeout(onClose, 300); // Close after 300ms (duration of the animation)
  };

  // Function to handle closing the sidebar without saving
  const handleClose = () => {
    setIsOpen(false); // Start closing animation
    setTimeout(onClose, 300); // Call the onClose function after 300ms
  };

  // If shouldRender is false, don't render anything
  if (!shouldRender) return null;

  return (
    <div
      className={`fixed right-0 top-0 h-full w-1/3 bg-white shadow-lg p-4 transform transition-transform ease-in-out duration-300 ${
        isOpen ? 'translate-x-0' : 'translate-x-full'
      }`}
    >
      <h2 className="text-xl font-bold mb-4">Edit Project</h2>

      {/* Input field for title */}
      <input
        type="text"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        className="border p-2 mb-4 block w-full rounded"
        placeholder="Title"
      />

      {/* Textarea for description */}
      <textarea
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        className="border p-2 mb-4 block w-full rounded"
        placeholder="Description"
      />

      {/* Dropdown for status */}
      <select
        value={status}
        onChange={(e) => setStatus(e.target.value as 'In Progress' | 'Completed' | 'On Hold')}
        className="border p-2 mb-4 block w-full rounded"
      >
        <option value="In Progress">In Progress</option>
        <option value="Completed">Completed</option>
        <option value="On Hold">On Hold</option>
      </select>

      {/* Save and Cancel buttons */}
      <div className="flex justify-end">
        <button onClick={handleClose} className="bg-gray-500 text-white py-2 px-4 rounded mr-2">
          Cancel
        </button>
        <button onClick={handleSave} className="bg-blue-500 text-white py-2 px-4 rounded">
          Save
        </button>
      </div>
    </div>
  );
};

export default Sidebar;
