import React from 'react';
import logo from './logo.svg';
import './App.css';
import { useState, useEffect } from 'react';
import ProjectForm from './components/ProjectForm';
import ProjectList from './components/ProjectList';


// Define project structure
interface Project {
  title: string;
  description: string;
  status: 'In Progress' | 'Completed' | 'On Hold';
}


const App = () => {
  const [projects, setProjects] = useState<Project[]>([]);

  useEffect(() => {
    const savedProjects = localStorage.getItem('projects');
    if (savedProjects) {
      setProjects(JSON.parse(savedProjects));
    }
  }, []);

  const addProject = (newProject: Project) => {
    const updatedProjects = [...projects, newProject];
    setProjects(updatedProjects);
    localStorage.setItem('projects', JSON.stringify(updatedProjects));
  };

  const reorderProjects = (reorderedProjects: Project[]) => {
    setProjects(reorderedProjects);
    localStorage.setItem('projects', JSON.stringify(reorderedProjects)); // Save the new order to local storage
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Project Status Dashboard</h1>
      <ProjectForm onAddProject={addProject} />
      <ProjectList projects={projects} onReorder={reorderProjects} />
    </div>
  );
};

export default App;
