import React, { useState, useEffect } from 'react';

interface Project {
  title: string;
  description: string;
  status: 'In Progress' | 'Completed' | 'On Hold';
}

interface SidebarProps {
  project: Project | null;
  onClose: () => void;
  onSave: (updatedProject: Project) => void;
}

const Sidebar = ({ project, onClose, onSave }: SidebarProps) => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [status, setStatus] = useState<'In Progress' | 'Completed' | 'On Hold'>('In Progress');
  const [isOpen, setIsOpen] = useState(false);

  useEffect(() => {
    if (project) {
      setTitle(project.title);
      setDescription(project.description);
      setStatus(project.status);
      setIsOpen(true); // Open sidebar when a project is provided
    } else {
      setIsOpen(false); // Close sidebar when no project
    }
  }, [project]);

  const handleSave = () => {
    if (project) {
      onSave({ title, description, status });
    }
    onClose();
  };

  return (
    <div
      className={`fixed right-0 top-0 h-full w-1/3 bg-white shadow-lg p-4 transform transition-transform ease-in-out duration-300 ${
        isOpen ? 'translate-x-0' : 'translate-x-full'
      }`}
    >
      {project && (
        <>
          <h2 className="text-xl font-bold mb-4">Edit Project</h2>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            className="border p-2 mb-4 block w-full rounded"
            placeholder="Title"
          />
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            className="border p-2 mb-4 block w-full rounded"
            placeholder="Description"
          />
          <select
            value={status}
            onChange={(e) => setStatus(e.target.value as 'In Progress' | 'Completed' | 'On Hold')}
            className="border p-2 mb-4 block w-full rounded"
          >
            <option value="In Progress">In Progress</option>
            <option value="Completed">Completed</option>
            <option value="On Hold">On Hold</option>
          </select>
          <div className="flex justify-end">
            <button onClick={onClose} className="bg-gray-500 text-white py-2 px-4 rounded mr-2">
              Cancel
            </button>
            <button onClick={handleSave} className="bg-blue-500 text-white py-2 px-4 rounded">
              Save
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default Sidebar;
