import { useState } from 'react';

interface ProjectFormProps {
  onAddProject: (project: { title: string; description: string; status: 'In Progress' | 'Completed' | 'On Hold' }) => void;
}

const ProjectForm = ({ onAddProject }: ProjectFormProps) => {
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [status, setStatus] = useState<'In Progress' | 'Completed' | 'On Hold'>('In Progress');

    const handleSubmit = (e: React.FormEvent) => {
      e.preventDefault();
      onAddProject({ title, description, status });
      setTitle('');
      setDescription('');
    };

    return (
      <form onSubmit={handleSubmit} className="mb-6">
        <input
          type="text"
          placeholder="Project Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          className="border p-2 mb-2 block w-full rounded"
          required
        />
        <textarea
          placeholder="Project Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          className="border p-2 mb-2 block w-full rounded"
          required
        />
        <select
          value={status}
          onChange={(e) => setStatus(e.target.value as 'In Progress' | 'Completed' | 'On Hold')}
          className="border p-2 mb-4 block w-full rounded"
        >
          <option value="In Progress">In Progress</option>
          <option value="Completed">Completed</option>
          <option value="On Hold">On Hold</option>
        </select>
        <button className="bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded transition duration-200">
          Add Project
        </button>
      </form>
    );
  };

  export default ProjectForm;
